import React, {useState, useEffect} from 'react';
import './App.css';
import Pokemon from "./components/Pokemon";

import axios from "axios";


function App() {
    const [pokemon, setPokemon] = useState([]);
    const [pokemonAbilities, setPokemonAbilities] = useState([])
    const [mooves, setMooves] = useState([])


    useEffect(() => {
        async function fetchData() {
            try {
                const result = await axios.get(`https://pokeapi.co/api/v2/pokemon/jigglypuff`);
                console.log(result.data);
                setPokemon(result.data)
                setPokemonAbilities(result.data.abilities)

                setMooves(result.data.moves)


            } catch (e) {
                console.error(e)
            }
        }

        fetchData();
    }, []);

    return (

        <Pokemon className="pokemon-container"
        name={pokemon.name}
        />
