import React, {useState, useEffect} from 'react';
import './App.css';
import Pokemon from "./components/Pokemon";
import logo from "../src/assets/Pik.png"

import axios from "axios";


function App() {
    const [pokemons, setPokemons] = useState([]);
    const [error, setError] = useState('')
    const [currentUrl, setCurrentUrl] = useState('https://pokeapi.co/api/v2/pokemon/')
    const [nextUrl, setNextUrl] = useState()
    const [previousUrl, setPreviousUrl] = useState(null)

//
    function handleClick() {
        setCurrentUrl(nextUrl)
        console.log(currentUrl)
    }


    useEffect(() => {

        async function fetchData() {

            setError('')
            try {
                const result = await axios.get(currentUrl);
                console.log(result.data.results);
                setPokemons(result.data.results);

                console.log(currentUrl)
                console.log(result.data)
                // console.log(result)
                setNextUrl(result.data.next)
                setPreviousUrl(result.data.previous)

            } catch (e) {
                setError("Er is iets misgegaan met het ophalen van de data.")
                console.error(e)
            }

        }

        fetchData();

    }, []);

    return (
        <>
            <div className="header">
                <img src={logo} alt="logo" className="logo"/>
            </div>
            <div className="button">
                <button type="button" className="single-button">Previous</button>
                <button type="button" className="single-button" onClick={handleClick}>Next</button>
            </div>

            <div className="pokemon-container">
                {error && <p>{error}</p>}

                {pokemons && pokemons.map((pokemon) => {
                    return <Pokemon name={pokemon.name}/>

                })}

            </div>

            {/*<Pokemon name="ditto"/>*/}
            {/*<Pokemon name="pikachu"/>*/}

        </>
    );
}

export default App;
