import React, {useState, useEffect} from 'react';
import './App.css';
import Pokemon from "./components/Pokemon";

import axios from "axios";


function App() {
    const [pokemon, setPokemon] = useState([]);
    const [pokemonAbilities, setPokemonAbilities] = useState([])
    const [versionDetails, setVersionDetails] = useState([])

useEffect(() => {
    async function fetchData() {
        try{
            const result = await axios.get(`https://pokeapi.co/api/v2/pokemon/ditto`);
            console.log(result.data);
            setPokemon(result.data)
            setPokemonAbilities(result.data.abilities)
            setVersionDetails(result.data.moves[0].version_group_details)

        }catch (e){
            console.error(e)
        }
    }

    fetchData();
},[]);

 return (

<div className="pokemon-container">
     <h2>{pokemon.name}</h2>
    <img src={pokemon.sprites.front_default}/>
    <p>{pokemon.order}</p>
    <h3> Lijst Abilities:</h3>
    <ul>
        {pokemonAbilities.map((pokemonAbility) =>{
            return <li key={pokemonAbility.ability.name}>{pokemonAbility.ability.name}</li>
        })}
    </ul>
    <p>Gewicht: {pokemon.weight}</p>
 <p>Aantal Moves: {versionDetails.length}</p>

</div>
 );
}

export default App;
