    <img src={pokemons.sprites.front_default}/>
    <p>{pokemons.order}</p>
    <ul>
        {pokemonAbilities.map((pokemonAbility) =>{
            return <li key={pokemonAbility.ability.name}>{pokemonAbility.ability.name}</li>
        })}
    </ul>
    <p>{pokemons.weight}</p>
    {/*<p>{pokemons.moves[0].move.version_group_details.length}</p>*/}




</div>


